package com.onyx.libedu.model;import com.onyx.android.sdk.data.model.BaseData;import java.util.ArrayList;import java.util.List;/** * Created by ming on 2016/11/2. */public class KnowledgePoint extends BaseData {    private int parentId;    private String name;    private List<KnowledgePoint> childrens = new ArrayList<>();    public int getParentId() {        return parentId;    }    public void setParentId(int parentId) {        this.parentId = parentId;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public List<KnowledgePoint> getChildrens() {        return childrens;    }    public void setChildrens(List<KnowledgePoint> childrens) {        this.childrens = childrens;    }}